  return (
   <>
    <div>
      <Header />

      <div className='grid grid-cols-12 '>

        <div className='grid col-span-2 '>
        <Sidebar />
        </div>

        <div className='grid grid-cols-4 gap-4 '>
        <Outlet/>
        </div>

      </div>

   </div>
   </>
  )
}

export default Layout
