import React from 'react'
import { useState } from "react";
import AddSale from './AddSale'

function Sales() {
  const [product, setProduct] = useState([]);
  const [showAddSale, setShowAddSale] = useState(false)

  const showSale = () => {
     setShowAddSale(!showAddSale)
  }

  useEffect(() => {
    fetchProducts();
  }, []);

  // const notifyWarn = () => toast.warning("Are You Sure", { position: toast.POSITION.TOP_CENTER });

  const fetchProducts = async () => {
    try {
      const res = await axios.get("http://localhost:4000/api/Product/get");
      setProduct(res.data);
    } catch (error) {
      console.log("PRODUCTS: ERROR", error);
      // setErrors(error.response.data);
    }
  };

  return (
   <div>


<div>
  <div class="">
     <div className="flex justify-between ">
             <div class="mt-5 text-lg font-bold leading-6 text-gray-900 ml-2">
                  Orders
             </div>

                <div class="flex justify-end gap-4  whitespace-nowrap px-4 py-2 text-end font-large text-gray-900">
                <button
                  class="inline-block rounded border border-blue-600 bg-blue-600 px-4 py-3 text-sm font-medium text-white hover:bg-transparent hover:text-indigo-600 focus:outline-none focus:ring "
                  href="/download"
                  onClick={showSale}
                >
                  Add Sales Details
                </button>
                {showAddSale && <AddSale />}
                </div>
      </div>



          <table class="min-w-full divide-y-2 divide-gray-200 text-md bg-white">
            <thead>
              <tr>
                <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                  Product Id
                </th>
                <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                  store Id
                </th>
                {/* <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                  Contact Number
                </th> */}
                <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                  Quantity
                </th>
                <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                  Sale Date
                </th>
                <th class="whitespace-nowrap px-4 py-2 text-left font-medium text-gray-900">
                 Total Sale Amount
                </th>
              </tr>
            </thead>

            <tbody class="divide-y divide-gray-200">
              <tr>
                <td class="whitespace-nowrap px-4 py-2  text-gray-900">
                  Richard Martin
                </td>
                <td class="whitespace-nowrap px-4 py-2 text-gray-700">kit kat</td>
                <td class="whitespace-nowrap px-4 py-2 text-gray-700">
                  9984756789
                </td>
                <td class="whitespace-nowrap px-4 py-2 text-gray-700">
                  richard@gmail.com
                </td>
                <td class="whitespace-nowrap px-4 py-2 text-gray-700">
                  Taking Return
                </td>
                <td class="whitespace-nowrap px-4 py-2 text-gray-700">
                  {" "}
                  13
                </td>
              </tr>


            </tbody>
          </table>
        </div>
      </div>

    </div>
  )
}

export default Sales
